import { Component, OnInit, ChangeDetectionStrategy } from '@angular/core';
import { DashboardDataService } from '../../services/dashboard-data.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class DashboardComponent implements OnInit {
  channelData: any;
  engagementMetrics: any;
  budgetMetrics: any;
  campaignEvents: any;
  errorMessage: string = '';

  startDate: string = '';
  endDate: string = '';

  constructor(private dashboardService: DashboardDataService) {}

  ngOnInit() {
    try {
      this.channelData = this.dashboardService.getChannelPerformance();
      this.engagementMetrics = this.dashboardService.getEngagementMetrics();
      this.budgetMetrics = this.dashboardService.getBudgetMetrics();
      this.campaignEvents = this.dashboardService.getCampaignEvents();

      // Initialize date range to last 7 days
      const today = new Date();
      const priorDate = new Date().setDate(today.getDate() - 7);
      this.startDate = new Date(priorDate).toISOString().split('T')[0];
      this.endDate = today.toISOString().split('T')[0];
    } catch (error) {
      this.errorMessage = 'An error occurred while loading dashboard data.';
      console.error(error);
    }
  }

  onDateChange() {
    // Validate date range max 15 days
    if (this.startDate && this.endDate) {
      const start = new Date(this.startDate);
      const end = new Date(this.endDate);
      const diffTime = Math.abs(end.getTime() - start.getTime());
      const diffDays = diffTime / (1000 * 60 * 60 * 24);
      if (diffDays > 15) {
        alert('Date range cannot exceed 15 days.');
        // Reset endDate to startDate + 15 days or today
        const maxEnd = new Date(start);
        maxEnd.setDate(start.getDate() + 15);
        this.endDate = maxEnd > new Date() ? new Date().toISOString().split('T')[0] : maxEnd.toISOString().split('T')[0];
      }
    }
    // TODO: Refresh data based on new date range
  }

  createCampaign() {
    alert('Create Campaign button clicked!');
    // TODO: Implement create campaign functionality
  }
}
<div class="top-bar p-4 flex items-center justify-between border-b border-gray-300">
  <div class="date-range-selector">
    <label for="dateRange" class="mr-2 font-semibold">Date Range:</label>
    <input
      type="date"
      id="startDate"
      name="startDate"
      max=""
      class="border border-gray-400 rounded px-2 py-1 mr-2"
      (change)="onDateChange()"
    />
    to
    <input
      type="date"
      id="endDate"
      name="endDate"
      max=""
      class="border border-gray-400 rounded px-2 py-1 ml-2"
      (change)="onDateChange()"
    />
  </div>
  <button
    class="create-campaign-btn bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
    (click)="createCampaign()"
  >
    Create Campaign
  </button>
</div>
