import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class DashboardDataService {
  getChannelPerformance() {
    return {
      labels: ['SMS', 'WhatsApp', 'Email', 'Push', 'RCS'],
      datasets: [
        {
          label: 'Sent',
          backgroundColor: '#42A5F5',
          data: [120, 90, 150, 80, 60]
        },
        {
          label: 'Delivered',
          backgroundColor: '#66BB6A',
          data: [110, 85, 145, 75, 55]
        }
      ]
    };
  }

  getEngagementMetrics() {
    return {
      openRate: 78,
      clickRate: 45,
      conversionRate: 20
    };
  }

  getBudgetMetrics() {
    return {
      labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
      datasets: [
        {
          label: 'Spend',
          data: [5000, 7000, 6500, 8000],
          borderColor: '#FFA726'
        },
        {
          label: 'Leads',
          data: [150, 200, 180, 220],
          borderColor: '#66BB6A'
        }
      ],
      cpl: 33.5,
      cpc: 5.6,
      cpm: 12.4
    };
  }

  getCampaignEvents() {
    return [
      { message: 'Campaign starting soon at 3:25 PM', time: '15:25' },
      { message: 'Campaign just started execution', time: '15:30' }
    ];
  }
}
